		}
	}

	// process images
	$templateCode=str_replace('<%%UPLOADFILE(id)%%>', '', $templateCode);
	$templateCode=str_replace('<%%UPLOADFILE(CreatedOn)%%>', '', $templateCode);
	$templateCode=str_replace('<%%UPLOADFILE(NickName)%%>', '', $templateCode);
	$templateCode=str_replace('<%%UPLOADFILE(Sex)%%>', '', $templateCode);
	$templateCode=str_replace('<%%UPLOADFILE(Age)%%>', '', $templateCode);
	$templateCode=str_replace('<%%UPLOADFILE(Status)%%>', '', $templateCode);
	$templateCode=str_replace('<%%UPLOADFILE(InterestedIn)%%>', '', $templateCode);
	$templateCode=str_replace('<%%UPLOADFILE(Pic1)%%>', ($noUploads ? '' : '<input type=hidden name=MAX_FILE_SIZE value=5273600>'.$Translation['upload image'].' <input type="file" name="Pic1" id="Pic1">'), $templateCode);
	$templateCode=str_replace('<%%UPLOADFILE(Pic2)%%>', ($noUploads ? '' : '<input type=hidden name=MAX_FILE_SIZE value=5242880>'.$Translation['upload image'].' <input type="file" name="Pic2" id="Pic2">'), $templateCode);
	$templateCode=str_replace('<%%UPLOADFILE(Pic3)%%>', ($noUploads ? '' : '<input type=hidden name=MAX_FILE_SIZE value=5242880>'.$Translation['upload image'].' <input type="file" name="Pic3" id="Pic3">'), $templateCode);
	$templateCode=str_replace('<%%UPLOADFILE(MoreInfo)%%>', '', $templateCode);
	$templateCode=str_replace('<%%UPLOADFILE(Contact)%%>', '', $templateCode);

